        public KugouBase(MarshalMemoryStream buffer, string name) : base(buffer, name) { }

        protected abstract byte[] Magic { get; }

        protected override void Process()
        {
            if (!Reader.ReadBytes(16).SequenceEqual(Magic))
                throw new InvalidDataException("File header is unexpected.");

            var skip = Reader.ReadUInt32();
            _ = _buffer.Seek(0x1c, SeekOrigin.Begin);
            var key = (stackalloc byte[_paddedKeySize]);
            Reader.Read(key[..0x11]);
            key[0x10] = 0;
            Simd.Align(key, 0x11);

            int step = Simd.LaneCount;
            if (step % 16 != 0)
                throw new InvalidOperationException("SIMD width is not a multiple of 16.");

            var box = (stackalloc byte[_root.Length]);
            int i_k;
            for (int i = 0; i < _root.Length; i += step)
            {
                i_k = i % 0x11;
                var r = new Vector<byte>(_root[i..(i + step)]);
                var k = new Vector<byte>(key[i_k..(i_k + step)]);
                (r ^ k).CopyTo(box[i..(i + step)]);
            }

            _buffer.Origin = skip;
            var data = _buffer.AsSimdPaddedSpan();
            var mask = (stackalloc byte[step]);
            int i_b;
            for (int i = 0; i < data.Length; i += step)
            {
                i_b = i % 0x110;
                GetMask(ref mask, i);
                var window = data[i..(i + step)];
                var v = new Vector<byte>(window);
                var b = new Vector<byte>(box[i_b..(i_b + step)]);
                var m = new Vector<byte>(mask);
                var x = v ^ b ^ m;
                // blocked by upstream: (x * 0x10) -> (x << 4)
                StepAppend(x ^ (x * 0x10), i, step).CopyTo(window);
            }
        }

        /// <param name="buffer">Length should be a multiple of 16.</param>
        /// <param name="indexOffset">Should be a multiple of 16.</param>
        private static void GetMask(ref Span<byte> buffer, long indexOffset)
        {
            for (int i = 0; i < buffer.Length; i += 16)
            {
                ulong offset = checked((ulong)(indexOffset + i));
                buffer[i..(i + 16)].Fill(GetMaskRecursive(offset));
            }
        }

        private static byte GetMaskRecursive(ulong offset) => offset < 0x110
            ? (byte)0x00
            : (byte)(_maskOdd[(offset >>= 4) % 0x110] ^ _maskEven[(offset >>= 4) % 0x110] ^ GetMaskRecursive(offset));

        protected virtual Vector<byte> StepAppend(Vector<byte> v, int i, int step) { return v; }
    }

