        protected override byte[] Magic => _magic;

        public KugouBasic(MarshalMemoryStream file, string name) : base(file, name) { }
    }

    public sealed class KugouViper : KugouBase
    {
        private static readonly byte[] _magic = { 0x05, 0x28, 0xbc, 0x96, 0xe9, 0xe4, 0x5a, 0x43, 0x91, 0xaa, 0xbd, 0xd0, 0x7a, 0xf5, 0x36, 0x31 };
        private static readonly byte[] _obf;

        static KugouViper()
        {
            ReadOnlySpan<byte> obf = stackalloc byte[]
            { 0x25, 0xdf, 0xe8, 0xa6, 0x75, 0x1e, 0x75, 0x0e, 0x2f, 0x80, 0xf3, 0x2d, 0xb8, 0xb6, 0xe3, 0x11, 0x00 };
            _obf = Simd.Align(obf);
        }

        public KugouViper(MarshalMemoryStream file, string name) : base(file, name) { }

        protected override byte[] Magic => _magic;

        protected override Vector<byte> StepAppend(Vector<byte> v, int i, int step)
        {
            var i_o = i % 0x11;
            var o = new Vector<byte>(_obf[i_o..(i_o + step)]);
            return v ^ o;
        }
    }
